mapper.select:
SELECT COUNT(*)
FROM dish
WHERE deleted = 0

    AND status = #{status}


    AND category_id = #{categoryId}


    AND name LIKE CONCAT('%', #{keyword}, '%')

SELECT
id, category_id, name, price, image, description,
status, stock, sales, deleted, created_at, updated_at
FROM dish
WHERE deleted = 0

    AND status = #{status}


    AND category_id = #{categoryId}


    AND name LIKE CONCAT('%', #{keyword}, '%')

ORDER BY id DESC
LIMIT #{offset}, #{size}

mapper.update:
UPDATE dish
SET deleted = 1, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT
id, name, description, image, price,
category_id, status, deleted, created_at, updated_at
FROM dish
WHERE deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND category_id = #{categoryId}


    AND status = #{status}

ORDER BY created_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM dish
WHERE deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND category_id = #{categoryId}


    AND status = #{status}
