mapper.update:
UPDATE sys_user
SET last_login_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT
id, username, password_hash, nickname, phone, avatar,
role, status, last_login_at, created_at, updated_at
FROM sys_user
WHERE 1=1

    AND username LIKE CONCAT('%', #{username}, '%')


    AND role = #{role}


    AND status = #{status}

ORDER BY created_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM sys_user
WHERE 1=1

    AND username LIKE CONCAT('%', #{username}, '%')


    AND role = #{role}


    AND status = #{status}
